import React, { useState } from 'react';

import 'bulma/css/bulma.css'
import axios from 'axios' 
import { get } from 'https';
let _list = [];

const List = () =>{
    const [participants, pdata] = useState([]);
    const [_getData, setGetData] = useState(true);

    const getData = () => {
        if(!_getData)return;
        axios.get('https://xo3dnghur7.execute-api.us-east-2.amazonaws.com/dev/registration_app_get_check_in_details?event_id=testkonfhubslsdeploymentb29912c5')
        .then(function (response) {
          // handle success
           pdata(response.data);
           _list = participants;
           setGetData(false)        
        })
        .catch(function (error) {
          // handle error
          setGetData(false)  
          console.log(error);
        })
    }

    const handleChange = (e) => {
        console.log(e);
        pdata(_list.filter(v => {
            return v.toLowerCase().indexOf(e.target.value.toLowerCase()) > -1
       }))

    }

    getData();
    return(
        <div className="list is-hoverable">
        <br />
        <input className="input" type="text" onChange={e => {handleChange(e.target.value)}} />
        <br/>
        {
            participants.map(e => {
                return(
                    <a className="list-item">
             <p>Name:{e.name}</p><p>Email-id:{e.email_id}</p>
             <p>Organisation:{e.organisation}</p>
             </a>
                )
            })
        }
       </div>
    )
}

const ShowList = (props) => {
    const [_data, setData] = useState(props.participants)
    if(_data.length){
        _data.map(e => {
            return(
             <a className="list-item">
             <p>Name:{e.name}</p><p>Email-id:{e.email_id}</p>
             <p>Organisation:{e.organisation}</p>
             </a>
            )
         })
    }
    else{
        return(<div></div>)
    }
}


export default List
