import React, { Component } from 'react'
import QrReader from 'react-qr-reader'
 let _pdata = "";

class Test extends Component {
  state = {
    result: 'No result'
  }

  handleScan = data => {
    if (data) {
      this.setState({
        result: data
      })
      const scanData = data;
      console.log(scanData);
      localStorage.setItem("scanData",scanData);
      _pdata = localStorage.getItem("scanData");
      _pdata = _pdata.split("Booking Id:");
      _pdata = _pdata[1];
      _pdata = _pdata.trim();
      console.log(_pdata);
    }
  }
  handleError = err => {
    console.error(err)
  }
  render() {
    return (
      <div>
        <QrReader
          delay={300}
          onError={this.handleError}
          onScan={this.handleScan}
          style={{ width: '100%' }}
        />
        <p>{this.state.result}</p>
      </div>
    )
  }

  componentDidMount() {
    fetch("https://xo3dnghur7.execute-api.us-east-2.amazonaws.com/dev/registration_app_user_check_in")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
}
}


export default Test;
