import React from 'react';
import useForm from 'react-hook-form';

const style = {
    marginBottom:'10px',
    marginLeft: '10px',
    marginRight: '10px'
}



const inputStyle = {
    marginTop:'5px',
    marginBottom:'5px'
}


const AddParticiant =()=>{


    const { register, handleSubmit, errors } = useForm(); // initialise the hook
    const onSubmit = data => {
      console.log(data);
    };


    return(
        <form onSubmit={handleSubmit(onSubmit)}>    

        <input className="input is-primary" name="firstname" ref={register}  style={inputStyle} /> {/* register an input */}

        <input className="input is-primary" name="lastname" ref={register({ required: true })} />
        {errors.lastname && 'Last name is required.'}

        <input className="input is-primary" name="age" ref={register({ pattern: /\d+/ })} />
        {errors.age && 'Please enter number for age.'}

        <input type="submit" />
      </form>

    )

}

export default  AddParticiant;
