import React, { Component } from 'react'
import QrReader from 'react-qr-reader'
import axios from 'axios' 
import 'bulma/css/bulma.css'


let _pdata = "";

let modalClass="modal is-active";

const modalStyle = {
    backgroundColor: 'white',
    zIndex: 1,
    borderRadius:'5px'
}

class Test extends Component {

  constructor(props){
    this.state = {
        modalClass:"modal"
    }
  }

  componentWillMount(){
    console.log(this)
  }
  handleScan = data => {
    if (data) {
      this.setState({
        result: data,  
      })
      const scanData = data;
      console.log(scanData);
      localStorage.setItem("scanData",scanData);
      _pdata = localStorage.getItem("scanData");
      if(_pdata == _pdata.indexOf("Booking Id:")){
        _pdata = _pdata.split("Booking Id:");
        _pdata = _pdata[1];
        _pdata = _pdata.trim();
        //modalClass="modal is-active";

      }

    }
  }
  handleError = err => {
    console.error(err)
  }
  render() {
    return (
      <div>
        <QrReader
          delay={300}
          onError={this.handleError}
          onScan={this.handleScan}
          style={{ width: '100%' }}
        />
        <div className={modalClass}>
        <div className="modal-background"></div>
        <div style={modalStyle}>
        <div className="modal-content">
         <p>{this.state.result}</p>
        </div>
        <a className="button is-success" onClick={() => this.onClickSendData()}>Check-In</a>
        <a className="button is-danger">close</a>
        </div>
       <button className="modal-close is-large" aria-label="close"></button>
      </div>  
      </div>
    )
  }

   onClickSendData(){
    console.log("hello")
    axios.post('https://xo3dnghur7.execute-api.us-east-2.amazonaws.com/dev/registration_app_user_check_in', {
            "booking_id": _pdata   
      })
      .then(function (response) {
        console.log(response);
        localStorage.clear();
        // this.setState({
        //     modalClass : "modal"
        // })
        console.log(this);
      })
      .catch(function (error) {
        console.log(error);
        localStorage.clear();
      });
}



}


export default Test;
