import React, { useState } from 'react';

import 'bulma/css/bulma.css'
import axios from 'axios' 
let _list = ""

const List = () =>{
    const [participants, pdata] = useState([]);
    const [_getData, setGetData] = useState(true);

    const getData = () => {
        if(_getData)
        axios.get('https://xo3dnghur7.execute-api.us-east-2.amazonaws.com/dev/registration_app_get_check_in_details?event_id=testkonfhubslsdeploymentb29912c5')
        .then(function (response) {
          // handle success
           console.log(response.data);
           pdata(response.data);
           setGetData(false)        
        })
        .catch(function (error) {
          // handle error
          setGetData(false)  
          console.log(error);
        })
        .finally(function () {
            setGetData(false)  
            pdata(_list) 
        });
    }
    getData();

    return(
        <div className="list is-hoverable">
        <ShowList participants={participants} />
       </div>
    )
}

const ShowList = (props) => {
    const [_data, setData] = useState(props.participants)
    console.log(_data.length)
    if(_data.length){
        _data.map(e => {
            return(
             <a className="list-item">
             <p>Name:{e.name}</p><p>Email-id:{e.email_id}</p>
             <p>Organisation:{e.organisation}</p>
             </a>
            )
         })
    }
    else{
        return(<div></div>)
    }
}


export default List
