import React from 'react';
import useForm from 'react-hook-form';

const style = {
    marginBottom:'10px',
    marginLeft: '10px',
    marginRight: '10px'
}



const inputStyle = {
    marginTop:'5px',
    marginBottom:'5px'
}


const AddParticiant =()=>{


    const { register, handleSubmit, errors } = useForm(); // initialise the hook
    const onSubmit = data => {
      console.log(data);
    };


    return(
        <form onSubmit={handleSubmit(onSubmit)}>    

        <input className="input is-primary" name="Name" placeholder='Name'   style={inputStyle} /> {/* register an input */}

        <input className="input is-primary" name="email"  placeholder='Email-id' style={inputStyle} ref={register({ required: true })}  />
        {errors.lastname && 'Email-id is required.'}

        <input className="input is-primary" name="phone"  placeholder='Phone Number' style={inputStyle} ref={register({ pattern: /\d+/ })}  />
        {errors.lastname && 'Phone number is required.'}
        <input className="input is-primary" name="organisation" placeholder='Organisation' style={inputStyle} ref={register({ required: true })}  />
        {errors.age && 'Please enter organisation.'}
        <input type="submit" />
      </form>

    )

}

export default  AddParticiant;
