import React, { Component } from 'react'
import QrReader from 'react-qr-reader'
import axios from 'axios' 


let _pdata = "";

class Test extends Component {

  state = {
    result: 'No result',
    modalClass: " "  

  }

  handleScan = data => {
    if (data) {
      this.setState({
        result: data,
        modalClass: " "  


      })
      const scanData = data;
      console.log(scanData);
      localStorage.setItem("scanData",scanData);
      _pdata = localStorage.getItem("scanData");
      if(_pdata == _pdata.indexOf("Booking Id:")){
        _pdata = _pdata.split("Booking Id:");
        _pdata = _pdata[1];
        _pdata = _pdata.trim();
        this.setState({
            modalClass: this.state.modalClass == " " ? " is-active" : " "
        }

        )
      }

    }
  }
  handleError = err => {
    console.error(err)
  }
  render() {
    return (
      <div>
        <QrReader
          delay={300}
          onError={this.handleError}
          onScan={this.handleScan}
          style={{ width: '100%' }}
        />
        <div class={"modal"+ this.state.modalClass}>
        <div class="modal-background"></div>
        <div class="modal-content">
         <p>{this.state.result}</p>
        </div>
       <button class="modal-close is-large" aria-label="close"></button>
</div>

      </div>
    )
  }

  componentDidMount() {
    axios.post('https://xo3dnghur7.execute-api.us-east-2.amazonaws.com/dev/registration_app_user_check_in', {
            "booking_id": _pdata   
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
}
}


export default Test;
