import React, { useState } from "react";
import axios from "axios";
import imgkonfhub from "../components/images/konfhub.png";

const style = {
  marginBottom: "10px",
  marginLeft: "10px",
  marginRight: "10px"
};

const inputBorder = {
  border: "1px solid dodgerblue"
};

const Login = props => {
  const [_userId, setUserId] = useState("");
  const [isLoading, setLoading] = useState(false);
  if (localStorage.getItem("eventId") != null) {
    props.history.push("/routele");
    console.log("Routing to scan page");
    return <div>loading...</div>;
  } else {
    const handleSubmit = e => {
      setLoading(true);
      axios
        .get(
          "https://dstc324xgg.execute-api.us-east-2.amazonaws.com/test/participants/registration-authentication/" +
            _userId
        )
        .then(function(response) {
          setLoading(false);
          if (response.data === 400) {
            window.alert("Event Does Not Exist");
            localStorage.clear();
            props.history.push("/login");
          } else {
            let event_details = JSON.stringify(response.data);
            localStorage.setItem("eventId", _userId.toLowerCase());
            localStorage.setItem("event_details", event_details);
            localStorage.setItem(
              "hosted_event_id",
              response.data.hosetd_event_id
            );
            props.history.push("/routele");
          }
        })
        .catch(function(error) {
          // handle error
          console.log(error);
        });
      e.preventDefault();
    };

    return (
      <div>
        <div className="bodyLogin">
          <div className="has-text-centered ">
            <div className="top"></div>
            <img src={imgkonfhub} width="200" alt="konfhub" />
          </div>
          <form onSubmit={handleSubmit}>
            <div className="field">
              <div className="top2"></div>
              <div style={style}>
                <input
                  className="input is-rounded "
                  style={inputBorder}
                  id="userId"
                  name="userId"
                  placeholder="Enter Event Id"
                  value={_userId}
                  onChange={e => {
                    setUserId(e.target.value);
                  }}
                  required
                />
              </div>
            </div>
            <div className="field">
              <div className="has-text-centered ">
                <button
                  type="submit"
                  disabled={isLoading}
                  className={
                    +isLoading
                      ? "button is-info is-normal is-rounded top3  is-loading"
                      : "button is-info is-normal is-rounded top3 "
                  }
                  style={style}
                >
                  Let's Go
                </button>
              </div>
            </div>
          </form>
        </div>
        <div className="top2"></div>
      </div>
    );
  }
};

export default Login;
