import React from 'react';
import useForm from 'react-hook-form';

const style = {
    marginBottom:'10px',
    marginLeft: '10px',
    marginRight: '10px'
}

const formStyle ={
    display:'block',
}


const AddParticiant =()=>{


    const { register, handleSubmit, errors } = useForm(); // initialise the hook
    const onSubmit = data => {
      console.log(data);
    };


    return(
        <form>
                <div>
                    <div style={style}>
                    <a className="button is-fullwidth ">Add Particiapnts</a>
                    </div>
                   <div className="field " style={formStyle} id='forms' >
                    <div className="control" style={style}>
                    <input className="input is-primary" type="text" placeholder="Name" ref={register} required/>
                    </div>
                    <div className="control" style={style}>
                    <input className="input is-primary" type="text" placeholder="Email-id" ref={register({ required: true })} required/>
                    {errors.lastname && 'Last name is required.'}
                    </div>
                    <div className="control" style={style}>
                    <input className="input is-primary" type="number" placeholder="Phone" required/>
                    </div>
                    <div className="control" style={style}>
                    <input className="input is-primary" type="text" placeholder="Organisation" required/>
                    </div>
                    <a className="button buttonCheck is-success" onSubmit={handleSubmit(onSubmit)}>Add</a>
                    </div>

                 </div>
                 </form>

    )






}

export default  AddParticiant;
