import React, { useState } from 'react';

import 'bulma/css/bulma.css'
import axios from 'axios' 
import { get } from 'https';
let _list = [];



const style = {
    width: '70%',
    marginLeft:'10px',
    marginRight:'10px',
    boxShadow: '0px 0px 6px'
}

const List = () =>{
    const [participants, pdata] = useState([]);
    const [_getData, setGetData] = useState(true);

    const getData = () => {
        if(!_getData)return;
        axios.get('https://xo3dnghur7.execute-api.us-east-2.amazonaws.com/dev/registration_app_get_check_in_details?event_id=testkonfhubslsdeploymentb29912c5')
        .then(function (response) {
          // handle success
           pdata(response.data);
           _list = participants;
           setGetData(false)        
        })
        .catch(function (error) {
          // handle error
          setGetData(false)  
          console.log(error);
        })
    }


    getData();
    return(
        <div className="list is-hoverable">
        <br />
        <input style={style}  className="input is-rounded search-box" type="text" placeholder="Start typing to search" onChange={e => {pdata(_list.filter(v => {
            return v.name.toLowerCase().indexOf(e.target.value.toLowerCase()) > -1
       }))
   }} />
        <br/>
        {
            participants.map(e => {
                return(
                    <a className="list-item">
             <p>Name:{e.name}</p><p>Email-id:{e.email_id}</p>
             <p>Organisation:{e.organisation}</p>
             </a>
                )
            })
        }
       </div>
    )
}

const ShowList = (props) => {
    const [_data, setData] = useState(props.participants)
    if(_data.length){
        _data.map(e => {
            return(
             <a className="list-item">
             <p>Name:{e.name}</p><p>Email-id:{e.email_id}</p>
             <p>Organisation:{e.organisation}</p>
             </a>
            )
         })
    }
    else{
        return(<div></div>)
    }
}


export default List
